// import React from "react";
import { createGlobalStyle } from 'styled-components';
// 각 폰트 파일 import
import Font_N from '../Assets/Fonts/Notable-Regular.woff';
import Font_PB from '../Assets/Fonts/Padauk-Bold.woff';
import Font_PR from '../Assets/Fonts/Padauk-Regular.woff';

export default createGlobalStyle`
    @font-face {
        font-family: "Font_test";
        src: local("Font_test"), url(${Font_N}) format('woff'); 
        font-weight: lighter;
    }
    @font-face {
        font-family: "Font_test";
        src: local("Font_test"), url(${Font_PB}) format('woff');
        font-weight: normal;
    }
    @font-face {
        font-family: "Font_test";
        src: local("Font_test"), url(${Font_PR}) format('woff');
        font-weight: bold;
    }
`;
